import React, { Fragment, useState } from 'react'

const Register = () => {
    // use state initialises objects default format. hook runs function on the given object
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        passwordConfirm: ''
    });

    const { name, email, password, passwordConfirm } = formData;

    // function runs from event, changing relevant form data for each input by selected name and it's corresponding value
    // ... operator preserves previous data from form, only changing states of given input
    const formChange = e => setFormData({ ...formData, [e.target.name]: e.targetValue })

    return (
        <Fragment>
            <h1>Sign Up</h1>
            <form>
                <label for="name">Name:</label>
                <input type="text" name="name" placeholder="Name" value={name} onChange={e => onChange(e)} />
                <label for="email">Email</label>
                <input type="email" name="email" placeholder="Email Address" value={email} onChange={e => onChange(e)} />
                <label for="email">Password</label>
                <input type="password" name="password" placeholder="Password" value={password} onChange={e => onChange(e)} />
                <label for="passwordVerify">Verify Password</label>
                <input type="password" name="passwordConfirm" placeholder="Verify Password" value={passwordConfirm} onChange={e => onChange(e)} />
            </form>
        </Fragment>
    )
}

export default Register;
