import React, { useEffect, Fragment } from 'react';
import { connect } from 'react-redux';
import { getUserInfo } from '../../actions/dashboard';
import PropTypes from 'prop-types';
import Review from './Review';

const Dashboard = (props) => {

    useEffect(() => {
        props.getUserInfo();
        console.log(props.userName);
    }, [])

    console.log(props.userReview);

    return (
        <Fragment>
            {(!props.loading ? (<div>
                <h1>LOADING...</h1>
            </div>
            ) : (<div>
                <h1>{props.userName}</h1>
                <p>{props.userEmail}</p>
                <p>{props.userUniversity}</p>
                <Review scores={props.userReview} />
            </div>
                ))}
        </Fragment>
    )
}

const mapStateToProps = state => {
    return {
        // From intial login/registration state
        isAuthenticated: state.register.isAuthenticated,
        userName: state.register.user.name,
        //From dashboard state
        loading: state.dashboard.loading,
        userEmail: state.dashboard.email,
        userUniversity: state.dashboard.university,
        userReview: state.dashboard.review[0]
    }
}

Dashboard.propTypes = {
    getUserInfo: PropTypes.func.isRequired,
}


export default connect(mapStateToProps, { getUserInfo })(Dashboard);
