import React from 'react';
import axios from 'axios';

class UniversityList extends React.Component {
    state = {
        loading: true,
        name: null,
        id: null
    }

    async componentDidMount() {
        const response = await axios.get('/api/university');
        const string = JSON.stringify(response.data.list);
        const uni = JSON.parse(string);
        this.setState({ name: uni[0].name, id: uni[0]._id, loading: false })
        console.log(uni);
    }

    render() {
        return <div>
            <ul>
                <li>{this.state.loading || !this.state.name ? (
                    <div>loading</div>
                ) : (<div>
                    {this.state.name}
                </div>)} </li>
            </ul>

        </div>
    }
}

export default UniversityList;
