import { GET_USER_INFO } from './types';
import axios from 'axios';

export const getUserInfo = () => async dispatch => {
    try {
        const res = await axios.get('/dashboard');
        dispatch({
            type: GET_USER_INFO,
            payload: res.data.user
        })

    } catch (err) {
        console.log(err.response.data)
    }
}
