import { GET_USER_INFO } from './types';
import axios from 'axios';

export const getUserInfo = () => async dispatch => {
    try {
        console.log('Get user information dispatch initiated');
        const res = await axios.get('api/dashboard');
        dispatch({
            type: GET_USER_INFO,
            payload: res.data.user
        })

    } catch (err) {
        console.log(err.response.data)
    }
}

export const postReview = (scores) => async dispatch => {
    try {
        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }
        const body = { scores };
        console.log(body);
        const res = await axios.post('api/reviews', body, config);
        dispatch(postSuccess(res));
    } catch (err) {
        dispatch(postFailure(err.response.data));
    }
}

export const postSuccess = (response) => ({
    type: POST_SUCCESS,
    payload: response
})

export const postFailure = (error) => ({
    type: POST_FAILURE,
    payload: error
})
