import React from 'react';

var values;

class UniversityList extends React.Component {

    constructor() {
        super();
        this.state = {
            options: []
        }
    }

    componentDidMount() {
        this.fetchOptions()
    }

    fetchOptions() {
        fetch('api/university')
            .then((res) => {
                return res.json();
            }).then((json) => {
                values = json;
                this.setState({ options: values.values })
                console.log(values);
            });
    }
    render() {
        return <div className="drop-down">
            <select>
                {this.state.options.map((option, key) => <option key={key} >{option}</option>)}
            </select>
        </div>;
    }
}

export default UniversityList;
