import React from 'react';
// import UniversityData from './universities.json';
import ReactDOM from 'react-dom';
import axios from 'axios';

class UniversityList extends React.Component {


    state = {
        values: []
    }

    componentDidMount() {
        fetch('/api/university')
            .then(function (res) {
                return res.json();
            }).then(function (json) {
                this.setState({
                    values: json
                })
            })
    }

    // UniversityData() {
    //     fetch('/api/university')
    //         .then(function (res) {
    //             return res.json();
    //         }).then(function (json) {
    //             this.setState({
    //                 values: json
    //             })
    //         })
    // }
    render() {
        return <div className="drop-down">
            <p>I would like to render a dropdown here from the values object</p>
            <select>{
                this.state.values.map((obj) => {
                    return <option value={obj.id}>{obj.name}</option>
                })
            }</select>
        </div>;
    }
}

export default UniversityList;
