import React from 'react';
import axios from 'axios';

class UniversityList extends React.Component {
    state = {
        loading: true,
        name: null,
        id: null
    }

    async componentDidMount() {
        const response = await axios.get('/api/university');
        const string = JSON.stringify(response.data.list);
        const uni = JSON.parse(string);
        this.setState({ name: uni[0].name, id: uni[0]._id })
        console.log(uni[0]._id);
    }

    render() {
        return <div>
            <ul>
                <li>{}</li>
            </ul>
            {this.state.loading ? <div>loading...</div> : <div> name..</div>}
        </div>
    }
}

export default UniversityList;
