import React, { Fragment, useState } from 'react';
import { Link } from 'react-router-dom'
import { connect } from 'mongoose';
import PropTypes from 'prop-types';
import Review from './Review';
import { deleteReview } from '../../actions/dashboard';

const ViewReview = (props, { deleteReview }) => {

    const [deleteCheck, deleteChange] = useState({
        delete: false,
    });

    const handleDelete = () => deleteChange(
        (prevState) => {
            return {
                delete: !prevState.delete
            }
        }
    )

    console.log(deleteCheck.delete);
    return (
        <Fragment>
            {(!props.review ? (<Link to="/submit">
                <button><h1>Submit a review</h1></button>
            </Link>) : (<Fragment>
                <h1>Your current review:</h1>
                <Review scores={props.review.scores} />
                <Link to="/submit">
                    <button><h1>Edit review</h1></button>
                </Link>
                <br />
                <Link to='/dashboard'>
                    <button onClick={handleDelete}><h1>Delete Review</h1></button>
                </Link>
                {(deleteCheck.delete === false ? (<Fragment></Fragment>) : (<div>
                    <p>Are you sure you want to delete?</p>
                    <button onClick={handleDelete, deleteReview()}>Yes</button>
                    <button onClick={handleDelete}>No</button>
                </div>))}
            </Fragment>))}
        </Fragment>
    )
}

ViewReview.propTypes = {
    deleteReview: PropTypes.func.isRequired
}

export default connect({ deleteReview })(ViewReview);
