import React, { Fragment, useEffect, useState } from 'react';
// import { Link } from 'react-router-dom';
// import axios from 'axios';
import PropTypes from 'prop-types'
import UniversityView from './UniversityView';
import { connect } from 'react-redux';
import { fetchUni } from '../../actions/university';



const Landpage = (props) => {

    const localState = useState({
        modalOpen: false,
        details: null
    })

    useEffect(() => {
        props.dispatch(fetchUni());
    }, []);

    const openSelection = (e) => {
        const selected = e.currentTarget.value;
        const data = props.universities.find(x => x._id === selected);
        localState.setState({ details: data });
        handleModalOpen();
    }

    const clearSelected = () => {
        localState.setState({ details: null })
    }

    const handleModalOpen = () => {
        this.setState((prevState) => {
            return {
                modalOpen: !prevState.modalOpen,
            }
        })
    }

    return (
        (!props.loading ? (
            < Fragment >
                <Fragment>
                    <UniversityView
                        details={localState.details}
                        // selected={this.state.selectedUni}
                        currentState={localState.modalOpen}
                        handleModalOpen={handleModalOpen}
                        clearSelected={clearSelected}>
                    </UniversityView >
                </Fragment>
                < div className="container" >
                    {/* <section className="landing">
                        {props.universities.reviews.map((obj, index) => {
                            return <button key={index} value={obj._id} onClick={e => { this.openSelection(e) }}>
                                <div className="university" key={index}>
                                    <div className="name" key={obj._id}>{obj.name}</div>
                                    {obj.scores.map((points) => {
                                        return <div className="points" key={index}> {points.total} </div>
                                    }
                                    )}
                                </div>
                            </button>
                        })}
                    </section> */}
                </div >
            </Fragment >
        ) : (<div><h1>LOADING</h1></div>))
    )
}


Landpage.protoTypes = {
    fetchUni: PropTypes.func.isRequired,
}

const mapStateToProps = state => {
    return {
        loading: state.uniFetch.loading,
        universities: state.uniFetch.universities
    }
}

export default connect(mapStateToProps)(Landpage);
