import { GET_USER_INFO, POST_BEGIN, POST_SUCCESS, POST_FAILURE } from '../actions/types';

const initialState = {
    email: null,
    university: null,
    review: {},
    loading: true,
    submission: {
        loading: false,
        success: null,
        error: null,
    }
}

export default function (state = initialState, actions) {
    const { type, payload } = actions;

    switch (type) {
        case GET_USER_INFO:
            return {
                email: payload.email,
                university: payload.userUniversity.name,
                review: payload.review.scores[0],
                loading: false
            }
        case POST_BEGIN:
            return {
                ...state,
                loading: true,
            }
        case POST_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
            }
        case POST_FAILURE:
            return {
                ...state,
                loading: false,
                success: false,
                error: payload.error
            }
        default:
            return state
    }
}
